(()=>{const e=firebase.auth(),n=(firebase.firestore(),document.getElementById("signup-form")),t=document.getElementById("log-out"),s=document.querySelector("#user"),i=document.querySelector("#signin-form");n.addEventListener("submit",(t=>{t.preventDefault();const i=n["signup-email"].value,r=n["signup-password"].value;e.createUserWithEmailAndPassword(i,r).then((e=>{s.innerHTML=e.user.email,n.reset()}))})),t.addEventListener("click",(function(n){n.preventDefault(),e.signOut().then((()=>{console.log("user signed out")})),s.innerHTML="Signed Out"})),i.addEventListener("submit",(n=>{n.preventDefault();const t=i["signin-email"].value,r=i["signin-password"].value;e.signInWithEmailAndPassword(t,r).then((e=>{s.innerHTML="Hello "+e.user.email,console.log("logged in"),i.reset()}))}))})();